package com.nmims.app.Adapters;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.nmims.app.Models.ICAComponentsDataModel;import com.nmims.app.R;import java.util.List;public class ViewInternalMarksDetailsRecyclerViewAdapter extends RecyclerView.Adapter<ViewInternalMarksDetailsRecyclerViewAdapter.MyViewHolder>{    private Context context;    private List<ICAComponentsDataModel> icaComponentsDataModelList;    public ViewInternalMarksDetailsRecyclerViewAdapter(Context context, List<ICAComponentsDataModel> icaComponentsDataModelList) {        this.context = context;        this.icaComponentsDataModelList = icaComponentsDataModelList;    }    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType)    {        View itemView = LayoutInflater.from(parent.getContext())                .inflate(R.layout.components_ica_details_design, parent, false);        return new MyViewHolder(itemView);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position)    {        ICAComponentsDataModel icaComponentsDataModel = icaComponentsDataModelList.get(position);        holder.ica_key.setText(icaComponentsDataModel.getComponentName());        holder.ica_value.setText(icaComponentsDataModel.getComponentMarks());    }    @Override    public int getItemCount() {        return icaComponentsDataModelList.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder    {        private TextView ica_key, ica_value;        public MyViewHolder(@NonNull View itemView)        {            super(itemView);            ica_key = itemView.findViewById(R.id.ica_key);            ica_value = itemView.findViewById(R.id.ica_value);        }    }}